import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { verifyOtpResponse } from 'src/app/models/api.models';
import { UserRole } from 'src/app/models/auth.model';
import { AuthService } from 'src/app/services/auth.service';
import { JwtService } from 'src/app/services/jwt.service';

@Component({
  selector: 'app-verify-login-otp',
  templateUrl: './verify-login-otp.component.html',
  styleUrls: ['./verify-login-otp.component.css']
})
export class VerifyLoginOtpComponent {
  formOtp:FormGroup;
  error_message:string = "";
  param:string | null = "";
  imageUrl:string = '../../../assets/tp.png'
  userdata!: verifyOtpResponse;
  private verifyLoginSubscription!:

  constructor(private _forms:FormBuilder,private _jwt:JwtService, private _router:Router, private _authService:AuthService, private _route:ActivatedRoute){

    this._route.queryParams.subscribe(params=>{
      this.param = params['phone'];
      console.log("vab",this.param);
  })
    this.formOtp = this._forms.group({
      otp: this._forms.control("", Validators.compose([Validators.minLength(4), Validators.maxLength(4),Validators.required])),
    })
  }
  verifyOtp(){

    this._authService.verifyOtp(this.formOtp.value,this.param).subscribe((result: any) => {
      console.log(result);
      this.userdata = result;
      if (this.userdata.status === "Success") {
        let role: UserRole = this.userdata.data.isAdmin ? UserRole.Admin : UserRole.User;
        this._jwt.setToken(this.userdata.token);
       this._jwt.setRole(role);
       this._jwt.setLocal(this.userdata.data);
       console.log("success");
       this._router.navigate(['user'])
        } else {
          this.error_message = "something went wrong"
          this._router.navigate(['auth/login.password']);
        }
    })
  }


  navigateToLoginWithPassword(){
    this._router.navigate(['auth/login.password'])
  }
}


<div class="flex justify-center md:justify-start pt-12 md:pl-12 md:-mb-24 ">
    <!-- <a href="#" class="bg-black text-white font-bold text-xl p-4">TripSync</a> -->
    <img [src]="imageUrl" alt="logo" class="h-20 w-28">
</div>

<div class="flex flex-col justify-center md:justify-start my-auto pt-8 md:mt-16 md:pt-0 px-8 md:px-24 lg:px-32">
    <p class="text-center text-3xl">Verify OTP</p>
    <form class="flex flex-col pt-3 md:pt-8" [formGroup]="formOtp" (submit)="verifyOtp()">
        <div class="flex flex-col pt-4">
            <label for="otp" class="text-lg">OTP</label>
            <input type="number" id="otp" formControlName="otp" placeholder="OTP" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline">
            <span *ngIf="formOtp.hasError('required','otp') && formOtp.controls['otp'].dirty" class="text-danger ps-2">*OTP is required</span>
            <span *ngIf="formOtp.hasError('maxLength','otp') && formOtp.controls['phone'].dirty" class="text-danger ps-2">*Enter valid OTP</span>
            <span *ngIf="formOtp.hasError('minLength','otp') && formOtp.controls['phone'].dirty" class="text-danger ps-2">*Enter a valid OTP</span>
        </div>
        <input type="submit" value="Verify" class="bg-black text-white font-bold text-lg hover:bg-gray-700 p-2 mt-8">
        <span *ngIf="error_message">{{error_message}}</span>
    </form>
    <div class="text-center pt-3 pb-6">
        <p>Login with password? <a (click)="navigateToLoginWithPassword()" class="underline font-semibold">Log in here.</a></p>
    </div>
</div>
