import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Route, Router } from '@angular/router';
import { sendOtpResponse } from 'src/app/models/api.models';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-forgot-password',
  templateUrl: './forgot-password.component.html',
  styleUrls: ['./forgot-password.component.css']
})
export class ForgotPasswordComponent implements OnInit{
  imageUrl:String ="";
  resetPassord!:FormGroup;
  response!: sendOtpResponse;
  error:st
  constructor(private _router:Router,private _form:FormBuilder,private _authService:AuthService){}
ngOnInit(): void {
  this.resetPassord = this._form.group({
    phone: this._form.control("", Validators.compose([Validators.required,Validators.pattern(/^\d{10}$/),Validators.minLength(10)])),
  })
}

  navigation(url:string){
    this._router.navigate([url]);
  }

  submit(){
    if(this.resetPassord.valid){
      this._authService.sendOtp(this.resetPassord.value).subscribe((result:sendOtpResponse)=>{
        console.log(result);
        this.response = result;
         if(this.response.status === 'Success'){
           this._router.navigate(['auth/verify.otp'],{ queryParams: { phone: this.response.data } });
         }else{
           this.error = this.response.message;
         }
      })
    }
  }
}
