  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {
  isSidebarExpanded = true;


  expandSidebar(){
    this.isSidebarExpanded = !this.isSidebarExpanded;
  }


}
