import { Component, Input } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { JwtService } from 'src/app/services/jwt.service';
import { VehicleService } from 'src/app/services/vehicle.service';

@Component({
  selector: 'app-vehicle',
  templateUrl: './vehicle.component.html',
  styleUrls: ['./vehicle.component.css']
})
export class VehicleComponent {
  @Input() vehicleInput!:FormGroup;
  vehicleData:FormGroup;
  brands:any;
  returnData:any;
  error:boolean=false;
  errorMessage:string = "";
  models:any;
  years:any;
  constructor(private _form:FormBuilder, private _vehicleService:VehicleService, private _jwt:JwtService, private _router:Router){

    this.vehicleData = this._form.group({
      type: this._form.control("", Validators.required),
      brand: this._form.control("", Validators.required),
      model: this._form.control("", Validators.required),
      year: this._form.control("", Validators.required),
      color: this._form.control("", Validators.required)
    })
  }
  onTypeValue(){
    console.log(this.vehicleData.value.type);
    this._vehicleService.getCarBrands(this.vehicleData.value.type).subscribe(result=>{
      this.brands = result
      console.log("types",this.brands.Results);
      this.brands.Results.forEach((element:any) => {
        console.log("carss",element.MakeName)
      });
    })
  }

  async onBrandValue(){
    console.log(this.vehicleData.value.brand);
    const url = 'https://api.api-ninjas.com/v1/cars?make='+`${this.vehicleData.value.brand}`;
    const options = {
      method: 'GET',

    headers: { 'X-Api-Key': 'WVH898cupeF73t3B4fElxA==xAuBmwhmgIXFI18K'},
    contentType: 'application/json',
    }

    const response:any = await fetch(url,options)
    const model =   await response.json();
    console.log(model);
    this.models = model;
  }

  async onModelValue(){
    const url = 'https://api.api-ninjas.com/v1/cars?model='+`${this.vehicleData.value.model}`;
    const options = {
      method: 'GET',

    headers: { 'X-Api-Key': 'WVH898cupeF73t3B4fElxA==xAuBmwhmgIXFI18K'},
    contentType: 'application/json',
    }

    const response:any = await fetch(url,options)
    const year =   await response.json();
    console.log(year);
    this.years = year;
  }

  submitVehicleData(){
    console.log(this.vehicleData.value);
  if (this.vehicleData.valid) {
    this._vehicleService.saveVehicleData(this.vehicleData.value).subscribe((result:any) => {
      this.returnData = result;
      console.log(this.returnData);
      if (this.returnData.status === "Success") {
        this._jwt.setToken(this.returnData.token);console.log("in succes")
        this._router.navigate(['add.vehicle.registartion'])
      } else {
        this.error = true;
        this.errorMessage = this.returnData.message;
      }
    })
  }else{
    this.error = true;
    this.errorMessage = "Form is not valid!";
  }
}
}


    <form class="flex flex-col  md:pt-8" [formGroup]="vehicleData" (submit)="submitVehicleData()" >
        <div class="flex flex-col pt-1">
            <label for="vechicleType" class="text-lg">Vehicle Type</label>
            <select  id="vechicleType" (change)="onTypeValue()" formControlName="type" placeholder="John Smith" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline" >
                <option value="Car">Car</option>
                <option value="Moto" >Two wheeler</option>
            </select>
            <span *ngIf="vehicleData.hasError('required','type')&& vehicleData.controls['type'].dirty"
            class="text-danger text-center ps-2 pt-2 col-12">*Type is required</span>
        </div>
        <div class="flex flex-col pt-1">
            <label for="brand" class="text-lg">Select a Brand</label>
            <select  id="brand"  formControlName="brand" (change)="onBrandValue()" placeholder="John Smith" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline" >
                <option value="{{brand.MakeName}}" *ngFor="let brand of brands.Results">{{brand.MakeName}}</option>
            </select>
            <span *ngIf="vehicleData.hasError('required','brand')&& vehicleData.controls['brand'].dirty"
            class="text-danger text-center ps-2 pt-2 col-12">*Brand is required</span>
        </div>
        <div class="flex flex-col pt-1">
            <label for="model" class="text-lg">Select a Model</label>
            <select  id="model"   formControlName="model" (change)="onModelValue()"  class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline" >
                <option value="{{item.model}}" *ngFor="let item of models">{{item.model}}</option>
            </select>
            <span *ngIf="vehicleData.hasError('required','model')&& vehicleData.controls['model'].dirty"
            class="text-danger text-center ps-2 pt-2 col-12">*Model is required</span>
        </div>
        <div class="flex flex-col pt-1">
            <label for="year" class="text-lg">Select a Year</label>
            <select  id="year"   formControlName="year"   class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline" >
                <option value="{{year.year}}" *ngFor="let year of years">{{year.year}}</option>
            </select>
            <span *ngIf="vehicleData.hasError('required','year')&& vehicleData.controls['year'].dirty"
            class="text-danger text-center ps-2 pt-2 col-12">*Year is required</span>
        </div>
        <div class="flex flex-col pt-1">
            <label for="year" class="text-lg">Color</label>
            <input  id="color"   formControlName="color"   class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline" >
            <span *ngIf="vehicleData.hasError('required','year')&& vehicleData.controls['year'].dirty"
            class="text-danger text-center ps-2 pt-2 col-12">*Year is required</span>
        </div>
        <input type="submit"  value="Submit" class="bg-cyan-300 w-1/4 text-gray-800 font-bold text-lg hover:bg-gray-700 p-2 mt-2 rounded" />
        <span class="text-center text-red-950" >Something went wrong!!</span>
    </form>
