import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { Vehicle } from '../models/vehicle_response.model';
import { Observable, flatMap, from, map } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class VehicleService {

  constructor(private _http:HttpClient) { }
  getCarBrands(type: string){
    return this._http.get(`https://vpic.nhtsa.dot.gov/api/vehicles/GetMakesForVehicleType/${type}?format=json`)
  }

  saveVehicleData(data:FormGroup){
    return this._http.post('http://localhost:3001/vehicle/save.vehicle',data);
  }

  saveRegistrationData(data:FormGroup){
    return this._http.post('http://localhost:3001/vehicle/save.registration.data',data);
  }

  saveInsuranceData(data:FormGroup){
    return this._http.post('http://localhost:3001/vehicle/save.insurance.data',data);
  }

  saveVehiclePhoto(files: FormData){
    return this._http.post('http://localhost:3001/vehicle/save.vehicle.photos',files);
  }

  //to get all vehicles data added in profile
  getVehiclesData(): Observable<Vehicle[]>{
    return this._http.get<Vehicle[]>('http://localhost:3001/vehicle/get.vehicles.info')
    .pipe(
      // Use RxJS to map the response to an array of Vehicle objects
      flatMap((response: any[]) => {
        return from(response) // Convert the array to an Observable
          .pipe(
            map((vehicleData: any) => {
              // Parse the vehicle data into a Vehicle object
              const vehicle: Vehicle = {
                _id
                category: vehicleData.category,
                userId: vehicleData.userId,
                brand: vehicleData.brand,
                model: vehicleData.model,
                yearOfManufacture: vehicleData.yearOfManufacture,
                color: vehicleData.color,
                registration: {
                  _id:vehicleData.registration._id,
                  registrationNumber: vehicleData.registration.registrationNumber,
                  expiryDate: new Date(vehicleData.registration.expiryDate),
                },
                insurance: {
                  _id:vehicleData.insurance._id,
                  insuranceCompany: vehicleData.insurance.insuranceCompany,
                  policyNumber: vehicleData.insurance.policyNumber,
                  expiryDate: new Date(vehicleData.insurance.expiryDate),
                },
                VehiclePhotos: vehicleData.VehiclePhotos,
                ratingAndReview: vehicleData.ratingAndReview.map((review: any) => {
                  return {
                    rating: review.rating,
                    review: review.review,
                  };
                }),
                isBlocked: vehicleData.isBlocked,
                isVerified: vehicleData.isVerified,
                createdAt: new Date(vehicleData.createdAt),
                updatedAt: new Date(vehicleData.updatedAt),
                status: vehicleData.status,
              };

              return vehicle;
            })
          );
      })
    );
  }

}
