import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import {  Router } from '@angular/router';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  otpLoginForm:FormGroup;
  error:string = "";
  response:{status:string,message:string,data:string};
constructor(private _router:Router,private _formlogin:FormBuilder, private _authService:AuthService){

  this.otpLoginForm = this._formlogin.group({
    phone: this._formlogin.control("", Validators.compose([Validators.required,Validators.pattern(/^\d{10}$/),Validators.minLength(10)])),
  })
}

  navigateToLoginWithPassword() {
    this._router.navigate(['auth/login.password'])
  }

  sendOtp(){
 if(this.otpLoginForm.valid){
      console.log(this.otpLoginForm.value);
      this._authService.sendOtp(this.otpLoginForm.value).subscribe((result: string)=>{
        console.log(result);
        this.response
        if(result === 'success'){
          this._router.navigate(['verify_otp']);
        }else{
          this.error = "User not registered!"
        }
      })
    }
  }
}


<div class="flex justify-center md:justify-start pt-12 md:pl-12 md:-mb-24 ">
    <a href="#" class="bg-black text-white font-bold text-xl p-4">TripSync</a>
</div>

<div class="flex flex-col justify-center md:justify-start my-auto pt-8 md:mt-16 md:pt-0 px-8 md:px-24 lg:px-32">
    <p class="text-center text-3xl">Welcome Back!</p>
    <form class="flex flex-col pt-3 md:pt-8" [formGroup]="otpLoginForm" (submit)="sendOtp()">
        <div class="flex flex-col pt-4">
            <label for="email" class="text-lg">Phone</label>
            <input type="email" id="email" formControlName="phone" placeholder="Phone" class="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 mt-1 leading-tight focus:outline-none focus:shadow-outline">
            <span *ngIf="otpLoginForm.hasError('required','phone') && otpLoginForm.controls['phone'].dirty" class="text-danger ps-2">*Phone number is required</span>
            <span *ngIf="otpLoginForm.hasError('pattern','phone') && otpLoginForm.controls['phone'].dirty" class="text-danger ps-2">*Enter valid phone number</span>
            <span *ngIf="otpLoginForm.hasError('minLength','phone') && otpLoginForm.controls['phone'].dirty" class="text-danger ps-2">*Enter a valid Phone number</span>
        </div>
        <input type="submit" value="Send OTP" class="bg-black text-white font-bold text-lg hover:bg-gray-700 p-2 mt-8">
    </form>
    <div class="text-center pt-3 pb-6">
        <p>Login with password? <a (click)="navigateToLoginWithPassword()" class="underline font-semibold">Log in here.</a></p>
    </div>
</div>
